import{_ as i,d as r}from"./app.c62b2955.js";var a="/images/fuwu/01.png",l="/images/fuwu/02.png",s="/images/fuwu/03.png",n="/images/fuwu/04.png",t="/images/fuwu/05.png",p="/images/fuwu/06.png",g="/images/fuwu/07.png",e="/images/fuwu/08.png";const o={},h=r('<h2 id="\u8C08\u8C08\u670D\u52A1\u6CBB\u7406\u4E0E\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u8C08\u8C08\u670D\u52A1\u6CBB\u7406\u4E0E\u5FAE\u670D\u52A1" aria-hidden="true">#</a> \u8C08\u8C08\u670D\u52A1\u6CBB\u7406\u4E0E\u5FAE\u670D\u52A1</h2><p>\u53D1\u8868\u4E8E 2017-06-27</p><p>\u8FD1\u671F\u90FD\u5728\u5927\u8C08\u5FAE\u670D\u52A1\uFF0C\u672C\u4EBA\u4E5F\u5728\u505A\u76F8\u5173\u7684\u5DE5\u4F5C\u5DF2\u7ECF\u4E24\u5E74\u5DE6\u53F3\u4E86\uFF0C\u505A\u4E86\u4E00\u4E2A\u5FAE\u670D\u52A1\u7684\u5206\u4EAB\uFF0C\u6574\u7406\u4E86\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u6709\u95EE\u9898\u53EF\u4EE5\u5728\u4E0B\u9762\u7559\u8A00\uFF0C\u53EF\u4EE5\u548C\u5927\u5BB6\u4E00\u8D77\u8BA8\u8BBA\u3002</p><p>\u5148\u7B80\u5355\u4ECB\u7ECD\u4E86\u4E92\u8054\u7F51\u67B6\u6784\u7684\u6F14\u53D8\uFF0C\u8FDB\u800C\u4ECB\u7ECD\u4E86\u670D\u52A1\u5316\uFF0C\u6700\u540E\u518D\u4ECB\u7ECD\u5FAE\u670D\u52A1\uFF0C\u5FAE\u670D\u52A1\u662F\u670D\u52A1\u6CBB\u7406\u7684\u5347\u7EA7\u4E5F\u662F\u4E92\u8054\u7F51\u67B6\u6784\u7684\u8FDB\u4E00\u6B65\u5EF6\u4F38\u3002</p><h2 id="\u4E92\u8054\u7F51\u67B6\u6784\u6F14\u53D8" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u67B6\u6784\u6F14\u53D8" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u67B6\u6784\u6F14\u53D8</h2><h3 id="\u5355\u4F53\u4F53\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u5355\u4F53\u4F53\u67B6\u6784" aria-hidden="true">#</a> \u5355\u4F53\u4F53\u67B6\u6784</h3><p>\u5728\u8BA1\u7B97\u673A\u8F6F\u4EF6\u53D1\u5C55\u65E9\u671F\uFF0C\u4E00\u822C\u684C\u9762\u8F6F\u4EF6\u90FD\u662F\u91C7\u7528\u8FD9\u79CD\u67B6\u6784\uFF0C\u4E0D\u7BA1\u662F\u754C\u9762\u8FD8\u662F\u4E1A\u52A1\u5904\u7406\u8FD8\u662F\u6570\u636E\u5904\u7406\u90FD\u653E\u5230\u4E00\u4E2A\u5305\u4E2D\u3002\u8FD9\u79CD\u5176\u5B9E\u8C08\u4E0D\u4E0A\u67B6\u6784\uFF0C\u4F46\u4E5F\u53EF\u4EE5\u8BF4\u662F\u5F88\u597D\u7684\u67B6\u6784\uFF0C\u56E0\u4E3A\u5B83\u8DB3\u591F\u7B80\u5355\u3002 \u5982\u4E0B\u56FE\uFF1A</p><p><a href="/images/fuwu/01.png"><img src="'+a+'" alt="image"></a><a href="/images/fuwu/02.png"><img src="'+l+'" alt="image"></a></p><ul><li><strong>\u5355\u4F53\u5F0F\u5E94\u7528\u7684\u4F18\u70B9\u660E\u663E\uFF1A</strong> \u73B0\u6709IDE\u90FD\u662F\u96C6\u6210\u5F00\u53D1\u73AF\u5883\uFF0C\u975E\u5E38\u9002\u5408\u5355\u4F53\u5F0F\u5E94\u7528\uFF0C\u5F00\u53D1\u3001\u7F16\u8BD1\u3001\u8C03\u8BD5\u4E00\u7AD9\u5F0F\u641E\u5B9A\u3002 \u4E00\u4E2A\u5E94\u7528\u5305\u542B\u6240\u6709\u529F\u80FD\uFF0C\u5BB9\u6613\u6D4B\u8BD5\u548C\u90E8\u7F72\u3002 \u8FD0\u884C\u5728\u4E00\u4E2A\u7269\u7406\u8282\u70B9\uFF0C\u73AF\u5883\u5355\u4E00\uFF0C\u8FD0\u884C\u7A33\u5B9A\uFF0C\u6545\u969C\u6062\u590D\u7B80\u5355\u3002</li><li><strong>\u5355\u4F53\u5F0F\u5E94\u7528\u7684\u7F3A\u70B9\u4E5F\u660E\u663E\uFF1A</strong> \u4E1A\u52A1\u8FB9\u754C\u6A21\u7CCA\uFF0C\u6A21\u5757\u804C\u8D23\u4E0D\u6E05\u6670\uFF0C\u5F53\u7CFB\u7EDF\u9010\u6E10\u53D8\u5927\uFF0C\u4EE3\u7801\u4F9D\u8D56\u590D\u6742\uFF0C\u96BE\u4EE5\u7EF4\u62A4\u3002 \u6240\u6709\u4EBA\u540C\u65F6\u5728\u4E00\u4E2A\u5DE5\u7A0B\u4E0A\u5F00\u53D1\uFF0C\u5BB9\u6613\u53D1\u751F\u4EE3\u7801\u4FEE\u6539\u51B2\u7A81\uFF0C\u4F9D\u8D56\u590D\u6742\u5BFC\u81F4\u9879\u76EE\u534F\u8C03\u56F0\u96BE\uFF0C\u5E76\u4E14\u5C40\u90E8\u4FEE\u6539\u5F71\u54CD\u4E0D\u53EF\u77E5\uFF0C\u9700\u8981\u5168\u8986\u76D6\u6D4B\u8BD5\uFF0C\u9700\u8981\u91CD\u65B0\u90E8\u7F72\uFF0C\u96BE\u4EE5\u652F\u6301\u5927\u56E2\u961F\u5E76\u884C\u5F00\u53D1\u3002 \u5F53\u7CFB\u7EDF\u5F88\u5927\u65F6\uFF0C\u7F16\u8BD1\u548C\u90E8\u7F72\u8017\u65F6\u3002 \u5E94\u7528\u6C34\u5E73\u6269\u5C55\u96BE\uFF0C\u4E00\u65B9\u9762\u72B6\u6001\u5728\u5E94\u7528\u5185\u90E8\u7BA1\u7406\uFF0C\u65E0\u6CD5\u900F\u660E\u8DEF\u7531\uFF1B\u53E6 \u4E00\u65B9\u9762\uFF0C\u4E0D\u540C\u6A21\u5757\u5BF9\u8D44\u6E90\u9700\u6C42\u5DEE\u5F02\u5927\uFF0C\u5F53\u4E1A\u52A1\u91CF\u589E\u5927\u65F6\uFF0C\u4E00\u89C6\u540C\u4EC1\u5730\u4E3A\u6240\u6709\u6A21\u5757\u589E\u52A0\u673A\u5668\u5BFC\u81F4\u786C\u4EF6\u6D6A\u8D39\u3002</li></ul><h3 id="mvc\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#mvc\u67B6\u6784" aria-hidden="true">#</a> MVC\u67B6\u6784</h3><p>\u4F46\u968F\u7740\u6D4F\u89C8\u5668\u7684\u51FA\u73B0\u4FBF\u4EA7\u751F\u4E86<strong>web</strong>\u5E94\u7528\uFF0C<strong>web</strong>\u5E94\u7528\u7684\u7279\u70B9\u662F\u754C\u9762\u90E8\u5206\u662F\u663E\u793A\u5728\u6D4F\u89C8\u5668\u4E2D\uFF0C\u670D\u52A1\u5904\u7406\u662F\u5728\u670D\u52A1\u5BB9\u5668\u4E2D\u7684\uFF0C\u9875\u9762\u663E\u793A\u4E00\u822C\u7528css+js+html\u6280\u672F\u6765\u5904\u7406\uFF0C\u800C\u540E\u7AEF\u53EF\u4EE5\u7528<strong>Java</strong>\u3001<strong>PHP\u7B49\u8BED\u8A00</strong>\uFF0C\u8FD9\u5C31\u4EA7\u751F\u4E86\u524D\u540E\u7AEF\u5206\u79BB\u3002\u5BF9\u4E8E<strong>web</strong>\u7CFB\u7EDF\uFF0C\u4E00\u4F53\u67B6\u6784\u96BE\u4EE5\u6EE1\u8DB3\u524D\u540E\u7AEF\u5206\u79BB\u7684\u5F00\u53D1\u9700\u6C42\uFF0C\u56E0\u800C\u4FBF\u4EA7\u751F\u4E86<strong>MVC</strong>\u67B6\u6784\u3002 <strong>\u4E3A\u4EC0\u4E48\u6211\u628AMvc\u4F46\u90A3\u5904\u5904\u5462\uFF0C\u56E0\u4E3A\u4ED6\u4E5F\u6709\u5212\u65F6\u4EE3\u7684\u610F\u4E49\uFF0C\u5176\u5B9E\u4ECE\u5927\u65B9\u5411\u8BB2MVC\u67B6\u6784\u4E5F\u662F\u5355\u4F53\u67B6\u6784\u7684\u4E00\u79CD</strong></p><p><strong>MVC\u67B6\u6784\u5982\u4E0B\u56FE\uFF1A</strong><a href="/images/fuwu/03.png"><img src="'+s+'" alt="image"></a></p><p>MVC\u624D\u7B97\u7684\u4E0A\u771F\u6B63\u610F\u4E49\u4E0A\u7684\u67B6\u6784\uFF0C\u56E0\u4E3A\u5B83\u9664\u4E86\u89E3\u51B3\u4E86\u524D\u540E\u7AEF\u5206\u79BB\u95EE\u9898\uFF0C\u8FD8\u5F15\u5165\u4E86\u4E00\u79CD\u5168\u65B0\u7684\u5F00\u53D1\u6A21\u5F0F\uFF0C\u7528\u4E00\u79CD\u4E1A\u52A1\u903B\u8F91\u3001\u6570\u636E\u3001\u754C\u9762\u663E\u793A\u5206\u79BB\u7684\u65B9\u6CD5\u7EC4\u7EC7\u4EE3\u7801\uFF0C\u4F7F\u5F97\u6574\u4E2A\u5E94\u7528\u5C42\u6B21\u66F4\u52A0\u5206\u660E\uFF0C\u800C\u4E14\u5404\u4E2A\u5C42\u6B21\u4E4B\u95F4\u4E0D\u4F46\u51CF\u4F4E\u4E86\u8026\u5408\u6027\uFF0C\u8FD8\u63D0\u9AD8\u4E86\u5404\u4E2A\u5C42\u6B21\u7684\u53EF\u91CD\u7528\u6027\u3002</p><p>\u4F46\u968F\u7740\u5E94\u7528\u89C4\u6A21\u7684\u4E0D\u65AD\u6269\u5927\uFF0C\u5E94\u7528\u6A21\u5757\u4E0D\u65AD\u589E\u52A0\uFF0C\u6574\u4E2A\u5E94\u7528\u4E5F\u663E\u5F97\u8D8A\u6765\u8D8A\u81C3\u80BF\uFF0C\u7EF4\u62A4\u8D77\u6765\u4E5F\u66F4\u52A0\u56F0\u96BE\uFF0C\u56E0\u6B64\u4FBF\u53C8\u4EA7\u751F\u4E86<strong>\u591A\u5E94\u7528\u67B6\u6784</strong>\u3002</p><h3 id="\u591A\u5E94\u7528\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u591A\u5E94\u7528\u67B6\u6784" aria-hidden="true">#</a> \u591A\u5E94\u7528\u67B6\u6784</h3><p>\u591A\u5E94\u7528\u67B6\u6784\u5F88\u7B80\u5355\uFF0C\u5C31\u662F\u628A\u539F\u6765\u7684\u5E94\u7528\u6309\u7167\u4E1A\u52A1\u7279\u70B9\u62C6\u5206\u6210\u591A\u4E2A\u5E94\u7528\u3002\u6BD4\u5982\u4E00\u4E2A\u5927\u578B\u7535\u5546\u7CFB\u7EDF\u53EF\u80FD\u5305\u542B\u7528\u6237\u7CFB\u7EDF\u3001\u5546\u54C1\u7CFB\u7EDF\u3001\u8BA2\u5355\u7CFB\u7EDF\u3001\u8BC4\u4EF7\u7CFB\u7EDF\u7B49\u7B49\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u4ED6\u4EEC\u72EC\u7ACB\u51FA\u6765\u5F62\u6210\u4E00\u4E2A\u4E2A\u5355\u72EC\u7684\u5E94\u7528\u3002\u591A\u5E94\u7528\u67B6\u6784\u7684\u7279\u70B9\u662F\u5E94\u7528\u4E4B\u95F4\u5404\u81EA\u72EC\u7ACB \uFF0C\u4E0D\u76F8\u4E92\u8C03\u7528\u3002<strong>\u5982\u4E0B\u56FE\uFF1A</strong><a href="/images/fuwu/04.png"><img src="'+n+'" alt="image"></a></p><p><strong>\u591A\u5E94\u7528\u867D\u7136\u89E3\u51B3\u4E86\u5E94\u7528\u81C3\u80BF\u95EE\u9898\uFF0C\u4F46\u5E94\u7528\u4E4B\u95F4\u76F8\u4E92\u72EC\u7ACB\uFF0C\u6709\u4E9B\u5171\u540C\u7684\u4E1A\u52A1\u6216\u4EE3\u7801\u65E0\u6CD5\u590D\u7528\u3002</strong></p><h3 id="\u5206\u5E03\u5F0F\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u67B6\u6784" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u67B6\u6784</h3><p>\u5BF9\u4E8E\u4E00\u4E2A\u5927\u578B\u7684\u4E92\u8054\u7F51\u7CFB\u7EDF\uFF0C\u4E00\u822C\u4F1A\u5305\u542B\u591A\u4E2A\u5E94\u7528\uFF0C\u800C\u4E14\u5E94\u7528\u4E4B\u95F4\u5F80\u5F80\u8FD8\u5B58\u5728\u5171\u540C\u7684\u4E1A\u52A1\uFF0C\u5E76\u4E14\u5E94\u7528\u4E4B\u95F4\u8FD8\u5B58\u5728\u8C03\u7528\u5173\u7CFB\u3002\u9664\u6B64\u4E4B\u5916 \uFF0C\u5BF9\u4E8E\u5927\u578B\u7684\u4E92\u8054\u7F51\u7CFB\u7EDF\u8FD8\u6709\u4E00\u4E9B\u5176\u5B83\u7684\u6311\u6218\uFF0C\u6BD4\u5982\u5982\u4F55\u5E94\u5BF9\u6025\u5267\u589E\u957F\u7684\u7528\u6237\uFF0C\u5982\u4F55\u7BA1\u7406\u597D\u7814\u53D1\u56E2\u961F\u5FEB\u901F\u8FED\u4EE3\u4EA7\u54C1\u7814\u53D1\uFF0C\u5982\u4F55\u4FDD\u6301\u4EA7\u54C1\u5347\u7EA7\u66F4\u52A0\u7A33\u5B9A\u7B49\u7B49 \u3002</p><p>\u56E0\u6B64\uFF0C\u4E3A\u4E86\u4F7F\u4E1A\u52A1\u5F97\u5230\u5F88\u597D\u7684\u590D\u7528\uFF0C\u6A21\u5757\u66F4\u52A0\u5BB9\u6613\u62D3\u5C55\u548C\u7EF4\u62A4\uFF0C\u6211\u4EEC\u5E0C\u671B\u4E1A\u52A1\u4E0E\u5E94\u7528\u5206\u79BB\uFF0C\u67D0\u4E2A\u4E1A\u52A1\u4E0D\u518D\u5C5E\u4E8E\u4E00\u4E2A\u5E94\u7528\uFF0C\u800C\u662F\u4F5C\u4E3A\u4E00\u4E2A\u72EC\u7ACB\u7684\u670D\u52A1\u5355\u72EC\u8FDB\u884C\u7EF4\u62A4\u3002\u5E94\u7528\u672C\u8EAB\u4E0D\u518D\u662F\u4E00\u4E2A\u81C3\u80BF\u7684\u6A21\u5757\u5806\u79EF\uFF0C\u800C\u662F\u7531\u4E00\u4E2A\u4E2A\u6A21\u5757\u5316\u7684\u670D\u52A1\u7EC4\u4EF6\u7EC4\u5408\u800C\u6210\u3002</p><p>\u5728\u5206\u5E03\u5F0F\u5E94\u7528\u67B6\u6784\u4E2D\uFF0C\u5E94\u7528\u76F8\u4E92\u72EC\u7ACB\uFF0C\u6BCF\u4E2A\u5E94\u7528\u4EE3\u7801\u72EC\u7ACB\u5F00\u53D1\uFF0C\u72EC\u7ACB\u90E8\u7F72\uFF0C\u5E94\u7528\u901A\u8FC7\u6709\u9650\u7684API\u63A5\u53E3\u4E92\u76F8\u5173\u8054\u3002API\u63A5\u53E3\u5C5E\u4E8E\u5E94\u7528\u4E00\u90E8\u5206\uFF0C\u4E00\u822C\u548C\u8868\u793A\u5C42\u5904\u4E8E\u540C\u4E00\u5C42\u6B21\uFF0C\u4E24\u8005\u5171\u4EAB\u4E1A\u52A1\u903B\u8F91\u5C42\uFF0CAPI\u548C\u8868\u793A\u5C42\u91C7\u7528\u540C\u6837\u7684web\u7AEF\u6280\u672F\uFF0C\u901A\u8BAF\u534F\u8BAE\u4E00\u822C\u4F7F\u7528HTTP\uFF0C\u6570\u636E\u683C\u5F0F\u662FJSON\uFF0C\u5E94\u7528\u96C6\u6210\u65B9\u5F0F\u6BD4\u8F83\u7B80\u5316\u3002</p><p>\u5206\u5E03\u5F0F\u67B6\u6784\u9996\u5148\u5BF9\u7CFB\u7EDF\u6309\u7167\u4E1A\u52A1\u8FDB\u884C\u5782\u76F4\u5207\u5206\uFF0C\u628A\u7CFB\u7EDF\u5207\u5206\u4E3A\u4E0D\u540C\u5E94\u7528\uFF0C\u9488\u5BF9\u8D44\u6E90\u9700\u6C42\u7279\u70B9\uFF08\u6BD4\u5982<strong>CPU/IO/\u5B58\u50A8\u5BC6\u96C6\u578B</strong>\uFF09\uFF0C\u901A\u8FC7\u52A0\u5F3A\u786C\u4EF6\u914D\u7F6E\u6EE1\u8DB3\u4E0D\u540C\u5E94\u7528\u7684\u9700\u6C42\uFF0C\u907F\u514D\u4E00\u5200\u5207\u65B9\u5F0F\u5E26\u6765\u7684\u8D44\u6E90\u6D6A\u8D39\u3002 <a href="/images/fuwu/05.png"><img src="'+t+'" alt="image"></a></p><p>\u6280\u672F\u4E0A\uFF0C<strong>API</strong>\u91C7\u7528\u6807\u51C6\u7684<strong>HTTP/JSON</strong>\u8FDB\u884C\u901A\u8BAF\uFF0C\u8C03\u7528\u53CC\u65B9\u5B9E\u73B0\u96BE\u5EA6\u90FD\u4E0D\u5927\uFF0C\u4F46\u662FAPI\u4E00\u822C\u662F\u201C\u88F8\u5954\u201D\u7684\uFF0C\u5728\u7CFB\u7EDF\u5C42\u9762\uFF0C\u8C03\u7528\u4F9D\u8D56\u5173\u7CFB\u4E0D\u900F\u660E\uFF0C\u8C03\u7528\u53EF\u9760\u6027\u7F3A\u4E4F\u4FDD\u969C\uFF0C\u56E0\u6B64\u53EA\u9002\u7528\u5E94\u7528\u4E4B\u95F4\u4F9D\u8D56\u94FE\u8DEF\u5C11\uFF0C\u8C03\u7528\u91CF\u4E0D\u5927\u7684\u7CFB\u7EDF\uFF0C\u5373\u5E94\u7528\u4E4B\u95F4\u8026\u5408\u786E\u5B9E\u591F\u677E\u7684\u7CFB\u7EDF\u3002</p><h3 id="soa\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#soa\u67B6\u6784" aria-hidden="true">#</a> SOA\u67B6\u6784</h3><p>\u6709\u4EBA\u8BF4<strong>SOA</strong>\u5C31\u662F\u5206\u5E03\u5F0F\uFF0C\u5176\u5B9E\u8FD9\u4E2A\u754C\u9650\u6709\u70B9\u6A21\u7CCA\uFF0C\u4E0D\u8FC7\u76F8\u6BD4\u8F83\u666E\u901A<strong>API</strong>\u65B9\u5F0F\uFF0C<strong>SOA\u67B6\u6784\u66F4\u8FDB\u4E00\u6B65</strong>\uFF1A</p><ul><li>\u6BCF\u4E2A<strong>service</strong>\u90FD\u662F\u6D53\u7F29\u7684\u7CBE\u534E\uFF0C\u805A\u7126\u67D0\u65B9\u9762\u6838\u5FC3\u4E1A\u52A1\uFF0C\u540C\u65F6\u4EE5\u590D\u7528\u7684\u65B9\u5F0F\u4F9B\u6574\u4E2A\u7CFB\u7EDF\u5171\u4EAB\u3002</li><li>\u670D\u52A1\u4F5C\u4E3A\u72EC\u7ACB\u7684\u5E94\u7528\uFF0C\u72EC\u7ACB\u90E8\u7F72\uFF0C\u63A5\u53E3\u6E05\u6670\uFF0C\u5F88\u5BB9\u6613\u505A\u81EA\u52A8\u5316\u6D4B\u8BD5\u548C\u90E8\u7F72\u3002</li><li>\u670D\u52A1\u662F\u65E0\u72B6\u6001\u7684\uFF0C\u5F88\u5BB9\u6613\u505A\u6C34\u5E73\u6269\u5C55\uFF1B\u901A\u8FC7\u5BB9\u5668\u865A\u62DF\u5316\u6280\u672F\uFF0C\u5B9E\u73B0\u6545\u969C\u9694\u79BB\u548C\u8D44\u6E90\u9AD8\u6548\u5229\u7528\uFF0C\u4E1A\u52A1\u91CF\u5927\u7684\u65F6\u5019\uFF0C\u52A0\u673A\u5668\u5373\u53EF\u3002</li><li>\u57FA\u4E8ESOA\u7684\u7CFB\u7EDF\u53EF\u4EE5\u6839\u636E\u670D\u52A1\u8FD0\u884C\u60C5\u51B5\uFF0C\u7075\u6D3B\u8C03\u63A7\u670D\u52A1\u8D44\u6E90\uFF0C\u5305\u62EC\u670D\u52A1\u4E0A\u4E0B\u67B6\u3001\u670D\u52A1\u5347\u964D\u7EA7\u7B49\uFF0C\u4F7F\u7CFB\u7EDF\u771F\u6B63\u5177\u5907\u53EF\u8FD0\u8425\u7684\u80FD\u529B\u3002</li><li>\u5F53\u7136\u5929\u4E0B\u6CA1\u6709\u514D\u8D39\u7684\u5348\u9910\uFF0C<strong>SOA</strong>\u4E5F\u5E26\u6765\u4E86\u989D\u5916\u590D\u6742\u6027\u548C\u5F0A\u7AEF\uFF1A</li><li>\u7CFB\u7EDF\u4F9D\u8D56\u590D\u6742\uFF0C\u7ED9\u5F00\u53D1/\u6D4B\u8BD5/\u90E8\u7F72\u5E26\u6765\u4E00\u7CFB\u5217\u6311\u6218\u3002</li><li>\u7AEF\u5230\u7AEF\u7684\u8C03\u7528\u94FE\u8DEF\u957F\uFF0C\u53EF\u9760\u6027\u964D\u4F4E\uFF0C\u4F9D\u8D56\u7F51\u7EDC\u72B6\u51B5\u3001\u670D\u52A1\u6846\u67B6\u53CA\u5177\u4F53service\u7684\u8D28\u91CF\u3002</li><li>\u5206\u5E03\u5F0F\u6570\u636E\u4E00\u81F4\u6027\u548C\u5206\u5E03\u5F0F\u4E8B\u52A1\u652F\u6301\u56F0\u96BE\uFF0C\u4E00\u822C\u901A\u8FC7\u6700\u7EC8\u4E00\u81F4\u6027\u7B80\u5316\u89E3\u51B3\u3002 \u7AEF\u5230\u7AEF\u7684\u6D4B\u8BD5\u548C\u6392\u969C\u590D\u6742\uFF0C<strong>SOA</strong>\u5BF9\u8FD0\u7EF4\u63D0\u51FA\u66F4\u9AD8\u8981\u6C42\u3002 \u4F8B\u5B50\u5982\u4E0B\uFF1A</li></ul><p><a href="/images/fuwu/06.png"><img src="'+p+'" alt="image"></a></p><ul><li><p><strong>SOA\u7684\u7279\u70B9</strong></p></li><li><p>\u53EF\u4ECE\u4F01\u4E1A\u5916\u90E8\u8BBF\u95EE</p></li><li><p>\u968F\u65F6\u53EF\u7528</p></li><li><p>\u7C97\u7C92\u5EA6\u7684\u670D\u52A1\u63A5\u53E3\u5206\u7EA7</p></li><li><p>\u677E\u6563\u8026\u5408</p></li><li><p>\u53EF\u91CD\u7528\u7684\u670D\u52A1</p></li><li><p>\u670D\u52A1\u63A5\u53E3\u8BBE\u8BA1\u7BA1\u7406</p></li><li><p>\u6807\u51C6\u5316\u7684\u670D\u52A1\u63A5\u53E3</p></li><li><p>\u652F\u6301\u5404\u79CD\u6D88\u606F\u6A21\u5F0F</p></li><li><p>\u7CBE\u786E\u5B9A\u4E49\u7684\u670D\u52A1\u5951\u7EA6 \u8BF4\u8D77SOA \u6211\u7B2C\u4E00\u60F3\u5230\u7684\u662F<strong>\u4F01\u4E1A\u670D\u52A1\u603B\u7EBF\uFF08ESB\uFF09</strong></p><h4 id="soa\u7684\u597D\u5904" tabindex="-1"><a class="header-anchor" href="#soa\u7684\u597D\u5904" aria-hidden="true">#</a> <strong>SOA\u7684\u597D\u5904</strong></h4><p><strong>\u90A3\u4E48SOA\u6709\u54EA\u4E9B\u597D\u5904\u5462\uFF1F</strong></p></li><li><p>\u67B6\u6784\u4E0A\u7CFB\u7EDF\u66F4\u52A0\u6E05\u6670</p></li><li><p>\u6838\u5FC3\u6A21\u5757\u7A33\u5B9A\uFF0C\u4EE5\u670D\u52A1\u7EC4\u4EF6\u4E3A\u5355\u4F4D\u8FDB\u884C\u5347\u7EA7\uFF0C\u907F\u514D\u4E86\u9891\u7E41\u53D1\u5E03\u5E26\u6765\u7684\u98CE\u9669</p></li><li><p>\u5F00\u53D1\u7BA1\u7406\u65B9\u4FBF</p></li><li><p>\u5355\u72EC\u56E2\u961F\u7EF4\u62A4\u3001\u5DE5\u4F5C\u5206\u660E\uFF0C\u804C\u8D23\u6E05\u6670</p></li><li><p>\u4E1A\u52A1\u590D\u7528\u3001\u4EE3\u7801\u590D\u7528</p></li><li><p>\u975E\u5E38\u5BB9\u6613\u62D3\u5C55</p></li><li><p><strong>SOA</strong>\u5B9E\u73B0\u65B9\u5F0F</p></li></ul><p>\u5982\u679C\u8981\u5B9E\u73B0SOA\u7684\u8BDD\uFF0C\u6700\u5E38\u7528\u7684\u65B9\u5F0F\u5C31\u662F\u5229\u7528<strong>RPC</strong>\u6846\u67B6\u3002\u56E0\u4E3A\u670D\u52A1\u7EC4\u4EF6\u4E00\u822C\u5206\u5E03\u5728\u4E0D\u540C\u7684\u670D\u52A1\u5668\u4E0A\uFF0C\u6240\u4EE5\u8981\u5B9E\u73B0\u670D\u52A1\u5316\u9700\u8981\u89E3\u51B3\u7684\u7B2C\u4E00\u4E2A\u95EE\u9898\u5C31\u662FRPC<strong>\u8FDC\u7A0B\u670D\u52A1\u8C03\u7528</strong>\u3002\u7C7B\u4F3C\u4E8E<strong>RPC</strong>\u65B9\u6848\u6709\u5F88\u591A\uFF0C\u6BD4\u5982\uFF1A</p><ul><li><strong>Java RMI</strong></li><li><strong>WebService</strong></li><li><strong>Hessian</strong></li><li><strong>Thrift</strong></li><li>\u2026</li><li>\u2026</li></ul><h3 id="\u670D\u52A1\u5316\u9762\u4E34\u7684\u6311\u6218" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u5316\u9762\u4E34\u7684\u6311\u6218" aria-hidden="true">#</a> \u670D\u52A1\u5316\u9762\u4E34\u7684\u6311\u6218</h3><p><strong>\u4E0A\u9762\u63D0\u5230\u8981\u5B9E\u73B0\u670D\u52A1\u5316\u9996\u5148\u9700\u8981\u89E3\u51B3\u8FDC\u7A0B\u670D\u52A1\u8C03\u7528\u95EE\u9898\uFF0C\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD8\u6709\u5F88\u591A\u5176\u4ED6\u95EE\u9898\u9700\u8981\u89E3\u51B3\u3002</strong></p><ul><li>\u670D\u52A1\u8D8A\u6765\u8D8A\u591A\uFF0C\u914D\u7F6E\u7BA1\u7406\u590D\u6742</li><li>\u670D\u52A1\u95F4\u4F9D\u8D56\u5173\u7CFB\u590D\u6742</li><li>\u670D\u52A1\u4E4B\u95F4\u7684\u8D1F\u8F7D\u5747\u8861</li><li>\u670D\u52A1\u7684\u62D3\u5C55</li><li>\u670D\u52A1\u76D1\u63A7</li><li>\u670D\u52A1\u964D\u7EA7</li><li>\u670D\u52A1\u9274\u6743</li><li>\u670D\u52A1\u4E0A\u7EBF\u4E0E\u4E0B\u7EBF</li><li>\u670D\u52A1\u6587\u6863</li><li>\u2026</li><li>\u2026 <strong>\u6709\u65F6\u95F4\u6211\u4E13\u95E8\u6709\u5199\u4E00\u7BC7\u5173\u4E8ERPC\u7684\u6587\u7AE0</strong> \u6211\u4EEC\u63A5\u7740\u5F80\u4E0B\u8BF4\uFF0C\u6709\u4E86\u670D\u52A1 \u5C31\u9700\u8981\u6CBB\u7406\u8DDF\u7EF4\u62A4 \u4E0B\u9762\u6211\u4EEC\u8BB2\u4E00\u8BB2\u670D\u52A1\u6CBB\u7406</li></ul><h2 id="\u670D\u52A1\u6CBB\u7406" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u6CBB\u7406" aria-hidden="true">#</a> \u670D\u52A1\u6CBB\u7406</h2><p>\u4E0A\u9762\u63D0\u5230\u4E86\u670D\u52A1\u5316\uFF0C\u5176\u5B9E\u8981\u60F3\u670D\u52A1\u5316\uFF0C\u670D\u52A1\u6CBB\u7406\u662F\u5173\u952E\u3002\u90A3\u4E48\u6709\u6CA1\u6709\u597D\u7684\u670D\u52A1\u6CBB\u7406\u65B9\u6848\u5462\uFF1F\u7B54\u6848\u662F\u6709\u7684\uFF0C\u800C\u4E14\u8FD8\u5F88\u591A\uFF0C\u6BD4\u5982\uFF1A <strong>ICE</strong> <strong>dubbo</strong> <strong>springcloud</strong> <strong>\u9B54\u6BEF</strong> \u7B49\u7B49 \u4E0B\u9762\u6211\u4EEC\u7B80\u5355\u8BF4\u8BF4\u670D\u52A1\u6CBB\u7406\u6240\u9700\u8981\u7684</p><ul><li><p>\u96C6\u4E2D\u7684\u6CE8\u518C\u4E2D\u5FC3\u548C\u5B58\u50A8\u5E93\uFF0C\u4EE5\u67E5\u627E\u548C\u53D1\u5E03\u4E0E\u670D\u52A1\u76F8\u5173\u7684\u6784\u4EF6\u548C\u5143\u6570\u636E\u3002</p></li><li><p>\u67E5\u627E\u6B63\u786E\u7684\u6388\u6743\u670D\u52A1\u3002</p></li><li><p>\u907F\u514D\u91CD\u590D\u5DE5\u4F5C\u3002</p></li><li><p>\u4FC3\u8FDB\u91CD\u7528\u3002</p></li><li><p>\u786E\u5B9A\u670D\u52A1\u5728 <strong>SOA</strong> \u751F\u547D\u5468\u671F\u4E2D\u7684\u5F53\u524D\u72B6\u6001\u3002</p></li><li><p>\u4E3A\u670D\u52A1\u8BA2\u9605\u8005\u63D0\u4F9B\u53EF\u89C1\u6027\u3002</p></li><li><p>\u786E\u5B9A\u76F8\u5173\u670D\u52A1\u548C\u66F4\u6539\u67D0\u4E2A\u670D\u52A1\u6240\u9020\u6210\u7684\u5F71\u54CD\u3002</p></li><li><p>\u4F20\u8FBE\u5BF9\u670D\u52A1\u6240\u505A\u7684\u66F4\u6539\u3002</p></li><li><p>\u7528\u4E8E\u8054\u7CFB\u548C\u5F3A\u5236\u5E94\u7528\u4E8E\u67D0\u4E2A\u670D\u52A1\u7684\u7B56\u7565\u7684\u673A\u5236\u3002\u7B56\u7565\u901A\u8FC7\u4F7F\u7528\u6CBB\u7406\u6846\u67B6\u6765\u5B9A\u4E49\u3002</p></li><li><p>\u5177\u6709\u751F\u547D\u5468\u671F\u611F\u77E5\u6027\u7684\u53EF\u81EA\u5B9A\u4E49\u7CFB\u7EDF\uFF0C\u8BE5\u7CFB\u7EDF\u5728\u751F\u547D\u5468\u671F\u4E2D\u53D1\u751F\u9636\u6BB5\u66F4\u6539\u65F6\u89E6\u53D1\u9A8C\u8BC1\uFF0C\u4EE5\u4FBF\u80FD\u591F\u81EA\u52A8\u5316\u9010\u4E2A\u9636\u6BB5\u7684\u6CBB\u7406\u9A8C\u8BC1\u3002</p></li><li><p>\u5728\u7406\u60F3\u7684\u60C5\u51B5\u4E0B\uFF0C\u6CE8\u518C\u4E2D\u5FC3\u5E94\u8BE5\u9488\u5BF9SOA\u8FD0\u884C\u65F6\u8FDB\u884C\u4F18\u5316\uFF0C\u4EE5\u4FBF\u80FD\u591F\u5728\u8FD0\u884C\u65F6\u671F\u95F4\uFF0C\u4F7F\u7528\u5B58\u50A8\u5728\u6CE8\u518C\u4E2D\u5FC3\u7684\u5143\u6570\u636E\u6765\u901A\u8FC7\u52A8\u6001\u8DEF\u7531\u5145\u5B9E\u5185\u5BB9\u3002</p><p>\u8BE6\u7EC6\u7684\u5404\u4E2A\u4E2D\u95F4\u4EF6\u7684\u539F\u7406\u4EE5\u53CA\u600E\u4E48\u89E3\u51B3\u670D\u52A1\u6CBB\u7406\u7684\u6211\u6709\u4E13\u95E8\u5199\u6587\u7AE0</p><p>\u7EE7\u7EED\u5F80\u4E0B\u8BF4 \u4ECE\u53BB\u5E74\u5FAE\u670D\u52A1\u7A81\u7136\u706B\u4E86\u8D77\u6765\uFF0C\u54B1\u4EEC\u770B\u770B\u5FAE\u670D\u52A1</p><h2 id="\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1" aria-hidden="true">#</a> \u5FAE\u670D\u52A1</h2></li></ul><p>\u5728\u5F88\u591A\u4EBA\u90FD\u5728\u8C08\u5FAE\u670D\u52A1\uFF0C\u90A3\u4E48\u5230\u5E95\u4EC0\u4E48\u662F\u5FAE\u670D\u52A1\u5462\uFF1F\u8FD9\u91CC\u8C08\u8C08\u6211\u5BF9\u5FAE\u670D\u52A1\u7684\u7406\u89E3\u3002</p><p>\u5FAE\u670D\u52A1\u6709\u4E24\u4E2A\u6838\u5FC3\uFF1A</p><ul><li><strong>\u5FAE\uFF1A\u670D\u52A1\u7684\u7C92\u5EA6\u8981\u7EC6\uFF0C\u5373\u670D\u52A1\u8981\u7EC6\u5316\u5230API(\u8981\u505A\u5230\u8FD9\u4E00\u70B9\u5F88\u4E0D\u5BB9\u6613\uFF0C\u6BD4\u5982\u670D\u52A1\u7684\u5207\u5206)</strong></li><li><strong>\u670D\u52A1\uFF1A\u63D0\u4F9B\u597D\u670D\u52A1\uFF0C\u8981\u8BA9\u7528\u6237\u611F\u5230\u597D\u7528</strong></li></ul><p>\u4E0A\u9762\u4E24\u4E2A\u6838\u5FC3\u603B\u7ED3\u8D77\u6765\uFF0C\u53EF\u4EE5\u7528\u4E0B\u9762\u8FD9\u5E45\u56FE\u8868\u793A\uFF1A <a href="/images/fuwu/07.png"><img src="'+g+'" alt="image"></a></p><p>\u4ECE\u4E0A\u9762\u8FD9\u5E45\u56FE\u770B\u51FA\uFF0C\u5FAE\u670D\u52A1\u7279\u522B\u7B80\u5355\uFF08\u597D\u7684\u67B6\u6784\u5C31\u5E94\u8BE5\u7B80\u5355\uFF09\uFF0C\u6211\u4EEC\u628A\u670D\u52A1\u518D\u62C6\u5206\u6210\u4E00\u4E2A\u4E2AAPI\uFF0CAPI\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u529F\u80FD\u3002\u7136\u540E\u6211\u4EEC\u628AAPI\u6254\u5230\u4E00\u4E2A\u201C\u4E91\u4E0A\u201D\uFF0C\u7136\u540E\u7528\u6237\u5C31\u53EF\u4EE5\u5230\u201C\u4E91\u4E0A\u201D\u83B7\u53D6\u6240\u6709API\u7684\u670D\u52A1\uFF0C\u8FD9\u4E2A\u201C\u4E91\u201D\u4FDD\u8BC1\u80FD\u63D0\u4F9B\u597D\u7684\u670D\u52A1\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\uFF0C\u6709\u4E86\u5FAE\u670D\u52A1\u4E4B\u540E\uFF0C\u670D\u52A1\u5BF9\u7528\u6237\u6765\u8BF4\u53D8\u5F97\u7279\u522B\u7B80\u5355\u3002\u4E0D\u518D\u9700\u8981\u53BB\u6CE8\u518C\u4E2D\u5FC3\u67E5\u627E\u670D\u52A1\uFF0C\u4E0D\u518D\u53BB\u505A\u9274\u6743\u5904\u7406\uFF0C\u4E0D\u7528\u62C5\u5FC3\u670D\u52A1\u6302\u6389\uFF0C\u4E0D\u7528\u62C5\u5FC3\u4E0D\u4F1A\u4F7F\u7528\u670D\u52A1\uFF0C\u6240\u6709\u7684\u95EE\u9898\u8FD9\u4E2A\u201C\u4E91\u201D\u90FD\u89E3\u51B3\u4E86\u3002\u8FD9\u4E5F\u662F\u5FAE\u670D\u52A1\u7684\u6838\u5FC3\u4E4B\u4E00\uFF0C\u63D0\u4F9B\u597D\u670D\u52A1\u3002 <a href="/images/fuwu/08.png"><img src="'+e+'" alt="image"></a></p><p>\u8BF4\u5230\u8FD9\u91CC\uFF0C\u5927\u5BB6\u5C31\u5E94\u8BE5\u5927\u4F53\u77E5\u9053\u8BE5\u600E\u4E48\u505A\u5FAE\u670D\u52A1\u4E86 \u5176\u5B9E\u201C\u4E91\u201D\u662F\u6700\u5173\u952E\u7684\u3002 \u5FAE\u670D\u52A1\u7684\u5173\u952E\u662F\u670D\u52A1\u7F51\u5173\uFF0C\u6240\u4EE5\uFF0C\u4E0A\u9762\u63D0\u5230\u7684\u201C\u4E91\u201D\u5C31\u662F\u670D\u52A1\u7F51\u5173\u3002\u8981\u505A\u5FAE\u670D\u52A1\uFF0C\u6211\u4EEC\u5148\u5B9A\u4E49\u4E00\u4E0B\u5FAE\u670D\u52A1\u9700\u8981\u5177\u5907\u7684\u7279\u70B9\u3002</p><h5 id="\u5FAE\u670D\u52A1\u7684\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1\u7684\u7279\u70B9" aria-hidden="true">#</a> \u5FAE\u670D\u52A1\u7684\u7279\u70B9</h5><ul><li>\u670D\u52A1\u9700\u8981\u518D\u7EC6\u5316\u6210API\uFF08\u670D\u52A1\u63A5\u53E3\u2014\u2014&gt;\u670D\u52A1API\uFF09</li><li>\u6BCF\u4E2A\u670D\u52A1\u7531\u4E00\u7EC4API\u7EC4\u6210</li><li>\u4EE5API\u5F62\u5F0F\u5BF9\u5916\u63D0\u4F9B\u7EDF\u4E00\u683C\u5F0F\u7684\u670D\u52A1</li><li>\u4F7F\u7528\u8005\u65E0\u9700\u4EFB\u4F55\u914D\u7F6E\uFF0C\u76F4\u63A5\u8C03\u7528\uFF08http\uFF09</li><li>\u5B8C\u6574\u7684API\u6587\u6863</li><li>API\u670D\u52A1\u5B89\u5168\u53EF\u9760\u7A33\u5B9A</li><li><strong>\u603B\u7ED3\uFF1A\u5927\u6982\u662F15\u5E748\u6708\u63A5\u89E6SOA\u5427\u4EE5\u4E0A\u5C31\u662F\u6211\u7684\u7C97\u6D45\u7406\u89E3\uFF0C\u4E0B\u4E00\u7BC7\u6211\u4F1A\u4ECB\u7ECD\u670D\u52A1\u6CBB\u7406\u7684\u51E0\u4E2A\u6846\u67B6</strong></li></ul>',45);function d(u,c){return h}var f=i(o,[["render",d],["__file","\u8C08\u8C08\u670D\u52A1\u6CBB\u7406\u4E0E\u5FAE\u670D\u52A1.html.vue"]]);export{f as default};
